xwikidoc.content:
1 Import All of the Curriki Wiki

This script allows to create an package.xml file from a directory of files ready for import.

<%
import com.xpn.xwiki.*;
import com.xpn.xwiki.plugin.packaging.*;

def exportdir = "c:/dev/java/gelc/gelcv1/gelc/src/main/xwiki"

if (request.confirm=="1") {
def packagexml = "<?xml version=\"1.0\" encoding=\"ISO-8859-1\"?>\n<package>\n<infos>\n<name>curriki</name>\n<description></description>\n<licence>LGPL</licence>\n<author>XWiki</author>\n<version>1.0.0</version>\n<backupPack>false</backupPack>\n</infos>\n<files>\n";
def mydir = new File(exportdir)
for (space in mydir.list()) {
   def spaceDir = new File(exportdir, space )
   if (spaceDir.isDirectory()&&(space!=".svn")) {
    println "* ${space}"
    for (page in spaceDir.list()) {
      if (page!=".svn") {
      def pageName = page;
      def language = ""
      def index = page.lastIndexOf(".")
      if (index!=-1) {
       language = page.substring(index+1)
       pageName = page.substring(0,index)
      }
      fullPageName = xwiki.getXMLEncoded("${space}.${pageName}")
      packagexml += "<file defaultAction=\"-1\" language=\"${language}\">${fullPageName}</file>\n"
      }
    }
   }
}
packagexml += "</files>\n</package>\n"

File outFile = new File(exportdir, "package.xml")
FileOutputStream fos = new FileOutputStream(outFile);
fos.write(packagexml.getBytes("ISO-8859-1"));
fos.flush();
fos.close();

println "package.xml written on file system. [Confirm importing>$doc.fullName?confirm=2]";
println "{code:xml}"
println packagexml
println "{code}"
}

if (request.confirm=="2") {
println "Starting import"
pack = new Package()
pack.setWithVersions(false)
pack.setBackupPack(true)
println "Reading documents"
pack.readFromDir(new File(exportdir), context.context)
println "Installing documents"
pack.install(context.context)
println "Finished."
} else {
 println "[Confirm creating package.xml>$doc.fullName?confirm=1] [Confirm importing>$doc.fullName?confirm=2]"
}
%>
