<script type="text/javascript">
/* <![CDATA[ */
function checkEmail() {
	var isValid = true;

	var emailStr = $('livesearch').value;
	if (emailStr.length <= 1) {
		alert("$msg.get("forgotUsername.email.mandatory")");
		isValid = false;
	} else {
		var atIndex = emailStr.indexOf("@");
		if ((atIndex < 1) || (emailStr.lastIndexOf(".") <= (atIndex+1)) || (emailStr.length <= (emailStr.lastIndexOf(".") + 1)) || (emailStr.lastIndexOf("@") != atIndex)) {
		alert("$msg.get("forgotUsername.email.invalid")");
		isValid = false;
	}
   }


  return isValid;
}
Event.observe('enterEmailForm', 'submit', function(event) {
  if (!checkEmail()) {
		event = event || window.event;
		if (typeof event.preventDefault != "undefined") {
			event.preventDefault();
		} else if (window.event){
			event.returnValue = false;
		}
  }
});
/* ]]> */
</script>
